import React from "react";
import './CamaraSelection.css';

const CameraSelection = ({ cameras, camera, onCameraSelected }) => {
  // Convertir el objeto 'cameras' en un array que react-select pueda entender
  const options = Object.keys(cameras).map(key => ({
    value: key,
    label: cameras[key]
  }));

  // Encontrar la opci贸n actualmente seleccionada basada en 'camera'
  const selectedOption = options.find(option => option.value === camera);

  return (
    <div className="CameraSelection-container">
      <Select
        value={selectedOption}
        onChange={selected => {
          // Llama a 'onCameraSelected' con el valor de la opci贸n seleccionada
          onCameraSelected(selected.value);
        }}
        options={options}
        classNamePrefix="react-select"
        data-testid="camera-select"
      />
    </div>
  );
};

export default CameraSelection;
