import React from "react";
import './CamaraSelection.css';

const CameraSelection = ({ cameras, camera, onCameraSelected }) => (
  <div className="CameraSelection-container">
    <select
      value={camera}
      className="CameraSelection-select"
      onChange={(e) => onCameraSelected(e.target.value)}
      data-testid="camera-select"
    >
      <option value="" disabled>Select a Camera</option>
      {Object.keys(cameras).map((key) => (
        <option value={key} key={key} style={{ backgroundImage: 'someFunGraphicOrColor()' }}>
          {cameras[key]}
        </option>
      ))}
    </select>
  </div>
);


export default CameraSelection;
