import React, { useState, useEffect } from 'react';
import './main.css';
import DateSlider from '../widgets/DateSlider';
import RoverSelector from '../widgets/RoverSelector';
import CameraSelection from '../widgets/CameraSelection';
import { isActive } from '../services/sols';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css'; // Asegúrate de importar los estilos de Lightbox

function RoverSearch(props) {
  const {
    rovers,
    cameras,
    camera,
    roverSelection,
    earth_date,
    photos,
    onCriteriaChange,
    onSearch,
  } = props;

  // Estado para controlar la apertura de la lightbox y el índice de la foto seleccionada
  const [isLightboxOpen, setIsLightboxOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const parallaxElement = document.querySelector('.App-header');
      if (parallaxElement) {
        parallaxElement.style.backgroundPositionY = `calc(50% - ${window.pageYOffset * 0.5}px)`;
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const setCriteriaAndPublish = (newCriteria) => {
    const merged = { ...props, ...newCriteria };
    onCriteriaChange(merged);
    onSearch(merged);
  };

  const roversActive = rovers.reduce((acc, curr) => {
    acc[curr.name.toLowerCase()] = isActive(curr.activePeriod[0], curr.activePeriod[1], earth_date);
    return acc;
  }, {});

  // Función para manejar el clic en la imagen
  const handleImageClick = (index) => {
    setPhotoIndex(index);
    setIsLightboxOpen(true);
  };

  return (
    <div>
      <header className="App-header">
        <h2>Mars Rover Images</h2>
      </header>
      <div className="container-fluid App-form">
        <div className="row">
          {/* Formulario y selecciones */}
          <form className="col-10 offset-1">
            <DateSlider earth_date={earth_date} onDateChanged={(date) => setCriteriaAndPublish({ earth_date: date })} />
            <RoverSelector roversActive={roversActive} rovers={rovers} roverSelection={roverSelection} onRoverSelection={(selected) => setCriteriaAndPublish({ roverSelection: selected })} />
            <CameraSelection camera={camera} cameras={cameras} onCameraSelected={(selectedCamera) => setCriteriaAndPublish({ camera: selectedCamera })} />
          </form>
        </div>
        <div className="row">
          <div className="col-10 offset-1">
            <div className="row">
              {photos.length === 0 ? (
                <p>No results</p>
              ) : (
                photos.map((photo, index) => (
                  <div key={photo.id} className="col-lg-4 col-12" onClick={() => handleImageClick(index)}>
                    <img className="App-img-result" src={photo.img_src} alt="Mars" style={{ cursor: 'pointer' }} />
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>
      {isLightboxOpen && (
        <Lightbox
          mainSrc={photos[photoIndex].img_src}
          nextSrc={photos[(photoIndex + 1) % photos.length].img_src}
          prevSrc={photos[(photoIndex + photos.length - 1) % photos.length].img_src}
          onCloseRequest={() => setIsLightboxOpen(false)}
          onMovePrevRequest={() => setPhotoIndex((photoIndex + photos.length - 1) % photos.length)}
          onMoveNextRequest={() => setPhotoIndex((photoIndex + 1) % photos.length)}
        />
      )}
    </div>
  );
}

export default RoverSearch;
