import React from "react";
import Select from 'react-select';
import './CameraSelection.css';

const customStyles = {
    control: (provided) => ({
        ...provided,
        background: 'rgba(255, 255, 255, 0.1)',

        boxShadow: 'none',

        cursor: 'pointer',
    }),
    singleValue: (provided) => ({
        ...provided,

    }),
    menu: (provided) => ({
        ...provided,
        background: 'rgba(0, 0, 0, 0.8)',
    }),
    option: (provided, state) => ({
        ...provided,
        background: state.isSelected ? '#555' : state.isFocused ? '#333' : 'transparent',
        color: '#ddd',
    }),
};

const CameraSelection = ({ cameras, camera, onCameraSelected }) => {
    const options = Object.keys(cameras).map(key => ({
        value: key,
        label: cameras[key]
    }));
    const selectedOption = options.find(option => option.value === camera);

    return (
        <div className="CameraSelection-container">
            <div className="CameraSelection-title">Choose a camera of the rover</div>
            <Select
                value={selectedOption}
                onChange={selected => onCameraSelected(selected.value)}
                options={options}
                styles={customStyles}
                classNamePrefix="react-select"
                data-testid="camera-select"
            />
        </div>
    );
};

export default CameraSelection;
