import React from "react";
import Select from 'react-select';
import './CameraSelection.css';

const customStyles = {
    control: (styles) => ({
        ...styles,
        backgroundColor: 'white',
        borderColor: '#ced4da',
        minHeight: '36px',
        fontWeight: 'normal',
        '&:hover': {
            borderColor: '#adb5bd'
        }
    }),
    option: (styles, { isFocused, isSelected }) => {
        return {
            ...styles,
            backgroundColor: isFocused ? '#f8f9fa' : isSelected ? '#e9ecef' : null,
            color: '#495057',
            cursor: 'pointer',
        };
    },
    // Ajusta otros estilos según sea necesario
};

const CameraSelection = ({ cameras, camera, onCameraSelected }) => {
    const options = Object.keys(cameras).map(key => ({
        value: key,
        label: cameras[key]
    }));
    const selectedOption = options.find(option => option.value === camera);

    return (
        <div className="CameraSelection-container">
            <div className="CameraSelection-title">Choose a camera from the rover:</div>
            <Select
                value={selectedOption}
                onChange={selected => onCameraSelected(selected.value)}
                options={options}
                styles={customStyles}
                classNamePrefix="react-select"
                data-testid="camera-select"
            />
        </div>
    );
};

export default CameraSelection;
